import React, { useState } from "react";
import { fetchResponse } from "../api/fetchResponse";
import "./Chatbot.css"; // Ensure you have the updated CSS
import SpeechRecognitionComponent from "./SpeechRecognition";
import SpeechSynthesisComponent from "./SpeechSynthesis";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);

  const handleUserMessage = async (text) => {
    setMessages((prev) => [...prev, { role: "user", content: text }]);

    const response = await fetchResponse(text);
    setMessages((prev) => [...prev, { role: "bot", content: response }]);

    // Speak the chatbot's response
    setTimeout(() => {
      SpeechSynthesisComponent.speak(response, () => {
        console.log("Finished speaking, resuming listening...");
      });
    }, 500);
  };

  return (
    <div className="chat-container">
      <div className="chat-header">üó£Ô∏è Companion Bot</div>

      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.role}`}>
            {msg.role === "user" ? "üë§" : "ü§ñ"} {msg.content}
          </div>
        ))}
      </div>

      {/* Speech Activation Button */}
      <div className="chat-input">
        <button
          className="mic-button"
          onClick={() => SpeechRecognitionComponent.startListening(handleUserMessage)}
        >
          üé§
        </button>
      </div>
    </div>
  );
};

export default Chatbot;
