import React, { useState } from "react";
import { fetchResponse } from "../api/fetchResponse";
import SpeechSynthesisComponent from "./SpeechSynthesis";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [isSpeaking, setIsSpeaking] = useState(false);

  const handleUserMessage = async (text) => {
    setMessages((prev) => [...prev, { role: "user", content: text }]);

    const response = await fetchResponse(text);
    setMessages((prev) => [...prev, { role: "bot", content: response }]);

    // Speak the chatbot's response
    setTimeout(() => {
      setIsSpeaking(true);
      SpeechSynthesisComponent.speak(response, () => {
        setIsSpeaking(false);
      });
    }, 500);
  };

  return (
    <div className="chat-container">
      <h1 className="title">ðŸ¤— Companion Bot</h1>

      <div className="chat-box">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`message ${msg.role === "user" ? "user" : "bot"}`}
          >
            {msg.role === "user" ? "ðŸ˜Š" : "ðŸ¤–"} {msg.content}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Chatbot;
