import React, { useState } from "react";
import { fetchResponse } from "../api/fetchResponse";
import SpeechRecognitionComponent from "./SpeechRecognition";
import SpeechSynthesisComponent from "./SpeechSynthesis";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);

  const handleUserMessage = async (text) => {
    setMessages((prev) => [...prev, { role: "user", content: text }]);

    const response = await fetchResponse(text);
    setMessages((prev) => [...prev, { role: "bot", content: response }]);

    setTimeout(() => {
      SpeechSynthesisComponent.speak(response);
    }, 500);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white p-6">
      <div className="w-full max-w-md bg-gray-850 p-6 rounded-2xl shadow-2xl flex flex-col space-y-4 h-[600px] overflow-hidden border border-gray-700 backdrop-blur-md">
        <h1 className="text-3xl font-extrabold text-gray-100 tracking-wide text-center">ðŸ’¬ Companion AI</h1>
        <div className="flex-grow overflow-y-auto scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-gray-800 p-2">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`p-3 rounded-xl text-sm max-w-[80%] break-words shadow-md ${
                msg.role === "user"
                  ? "bg-blue-500 text-white self-end"
                  : "bg-green-500 text-white self-start"
              }`}
            >
              {msg.content}
            </div>
          ))}
        </div>
        <div className="flex justify-center mt-4">
          <SpeechRecognitionComponent onResult={handleUserMessage} />
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
