const SpeechSynthesisComponent = {
  speak: (text) => {
    const speech = new SpeechSynthesisUtterance(text);
    speech.lang = "en-US";
    speech.rate = 1.8; // Increase speech speed (default is 1.0)
    speech.pitch = 1.2; // Slightly higher pitch for a female-like voice
    speech.volume = 1.0; // Full volume for clarity

    // Select a female voice
    const voices = window.speechSynthesis.getVoices();
    const femaleVoice = voices.find((voice) => 
      voice.name.includes("Female") || voice.name.includes("Samantha") || voice.name.includes("Google UK English Female")
    );

    if (femaleVoice) {
      speech.voice = femaleVoice;
    }

    window.speechSynthesis.speak(speech);
  },
};

// Ensure voices are loaded before speaking
window.speechSynthesis.onvoiceschanged = () => {
  SpeechSynthesisComponent.speak(""); // Preload voices (optional)
};

export default SpeechSynthesisComponent;
