sourcesContent:
export const fetchResponse = async (text) => {
  try {
    const apiKey = process.env.REACT_APP_PERPLEXITY_API_KEY; // Load from .env

    const response = await fetch("https://api.perplexity.ai/chat/completions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${apiKey}`, // Securely using the key
      },
      body: JSON.stringify({
        model: "sonar-pro",
        messages: [
          {
            role: "system",
            content: "You are an elder companion chatbot called 'Jing Xuan'.\
                      Speak in a warm but direct manner. \
                      Keep your responses short and easy to understand. \
                      Reply in only 1 sentence. \
                      Do not provide any citations.",
          },
          {
            role: "user",
            content: text,
          },
        ],
      }),
    });

    const data = await response.json();
    return data.choices ? data.choices[0].message.content : "Sorry, I didn't understand.";
  } catch (error) {
    console.error("Error fetching chatbot response:", error);
    return "I'm having trouble connecting right now.";
  }
};
