import React, { useState } from "react";
import { fetchResponse } from "../api/fetchResponse";
import SpeechRecognitionComponent from "./SpeechRecognition";
import SpeechSynthesisComponent from "./SpeechSynthesis";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);

  const handleUserMessage = async (text) => {
    setMessages((prev) => [...prev, { role: "user", content: text }]);

    const response = await fetchResponse(text);
    setMessages((prev) => [...prev, { role: "bot", content: response }]);

    // Speak the chatbot's response
    setTimeout(() => {
      SpeechSynthesisComponent.speak(response);
    }, 500);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-900 text-white p-4">
      <h1 className="text-3xl font-bold mb-4">üßë‚Äçüíª Companion Bot</h1>
      <div className="w-full max-w-md bg-gray-800 p-4 rounded-lg shadow-lg flex flex-col space-y-3 h-[500px] overflow-y-auto">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`p-3 rounded-lg w-fit max-w-[80%] ${
              msg.role === "user"
                ? "bg-blue-600 self-end"
                : "bg-green-600 self-start"
            }`}
          >
            {msg.content}
          </div>
        ))}
      </div>
      <div className="mt-4">
        <SpeechRecognitionComponent onResult={handleUserMessage} />
      </div>
    </div>
  );
};

export default Chatbot;
