const SpeechSynthesisComponent = {
  speak: (text) => {
    const synth = window.speechSynthesis;
    const speech = new SpeechSynthesisUtterance(text);

    // Set default voice settings
    speech.lang = "en-SG"; // Singapore English
    speech.rate = 1.2;  // Moderate speed for natural flow
    speech.pitch = 1.2; // Slightly higher pitch for a female voice
    speech.volume = 1.0; // Full volume

    // Wait for voices to be loaded and then select the appropriate voice
    const voices = synth.getVoices();

    // Try to find a Singaporean English female voice
    const singaporeVoice = voices.find(
      (voice) =>
        voice.lang === "en-SG" ||  // Look for a voice specifically marked as "en-SG"
        (voice.lang.startsWith("en") && voice.name.includes("Female")) ||  // Try other English female voices
        voice.name.toLowerCase().includes("singapore")  // Some browsers may list it differently
    );

    if (singaporeVoice) {
      speech.voice = singaporeVoice;
      console.log(`Using voice: ${singaporeVoice.name}`);
    } else {
      console.warn("Singaporean voice not found, using default voice.");
    }

    synth.speak(speech);
  },
};

export default SpeechSynthesisComponent;
